function BeyondJS(a){this.cfg={future:false,now:null,locale:{s:"second",S:"seconds",m:"minute",M:"minutes",h:"hour",H:"hours",d:"day",D:"days",w:"week",W:"weeks",mo:"month",MO:"months",y:"year",Y:"years","default":"just now",past:"ago",future:"from now"}};this.map={31536000:"y",2678400:"mo",604800:"w",86400:"d",3600:"h",60:"m",1:"s"};if(a){for(arg in a){this.cfg[arg]=a[arg]}}this.parse=function(b){if(b==null||b==""){return null}if(!isNaN(b-0)){return this.parseUnix(b)}try{var f=new Date(b)}catch(c){return null}return this.parseUnix(Math.round(f.getTime()/1000))};this.parseUnix=function(e){var d=this.cfg.now||Math.round(new Date().getTime()/1000);var f=e-d;if(f>0&&!this.cfg.future){return null}var b=Math.abs(f);var g=0;var c="";for(boundary in this.map){if(b>=boundary){g=Math.floor(b/boundary);c=this.map[boundary];break}}if(g==0){return this.cfg.locale["default"]}else{if(g>1){c=c.toUpperCase()}}return g+" "+this.cfg.locale[c]+" "+this.cfg.locale[(f<0?"past":"future")]}};